                  className="w-full justify-start"
                  onClick={() => setLocation("/booking")}
                >
                  <Camera className="mr-2" size={16} />
                  Create New Booking
                </Button>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="w-full justify-start"
                  onClick={() => setLocation("/gallery")}
                >
                  <Eye className="mr-2" size={16} />
