@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground overflow-x-hidden;
  }

  .constellation-bg {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 0;
    overflow: hidden;
  }

  .particle {
    position: absolute;
    width: 3px;
    height: 3px;
    background: linear-gradient(45deg, var(--jamaica-green), var(--jamaica-yellow));
    border-radius: 50%;
    animation: float 6s infinite linear;
    opacity: 0.7;
  }

  .floating-shapes {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: 1;
  }

  .shape {
    position: absolute;
    opacity: 0.1;
    animation: floatShapes 20s infinite linear;
  }

  @keyframes float {
    0% { 
      transform: translateY(100vh) rotate(0deg); 
      opacity: 0; 
    }
    10% { opacity: 1; }
    90% { opacity: 1; }
    100% { 
      transform: translateY(-10vh) rotate(360deg); 
      opacity: 0; 
    }
  }

  @keyframes floatShapes {
    0% { transform: translateY(100vh) rotate(0deg); }
    100% { transform: translateY(-100vh) rotate(360deg); }
  }

  .animate-glow {
    animation: glow 2s infinite alternate;
  }

  @keyframes glow {
    from { box-shadow: 0 0 20px hsl(var(--primary) / 0.3); }
    to { box-shadow: 0 0 30px hsl(var(--secondary) / 0.6), 0 0 40px hsl(var(--primary) / 0.3); }
  }

  .hover-3d {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .hover-3d:hover {
    transform: translateY(-5px) rotateX(5deg);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  }

  .magnetic-btn {
    transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .magnetic-btn:hover {
    transform: translateY(-2px) scale(1.05);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
  }

  .counter-animation {
    font-variant-numeric: tabular-nums;
  }

  @keyframes slideInUp {
    from { transform: translateY(50px); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }

  .slide-in-up {
    animation: slideInUp 0.6s ease-out forwards;
  }

  .stagger-1 { animation-delay: 0.1s; }
  .stagger-2 { animation-delay: 0.2s; }
  .stagger-3 { animation-delay: 0.3s; }
  .stagger-4 { animation-delay: 0.4s; }

  .gradient-text {
    background: linear-gradient(135deg, var(--jamaica-green), var(--jamaica-yellow));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    color: transparent;
  }

  .package-card {
    background: linear-gradient(135deg, var(--card) 0%, var(--muted) 100%);
    border: 2px solid transparent;
    background-clip: padding-box;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
  }

  .package-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, var(--jamaica-green), var(--jamaica-yellow));
    border-radius: inherit;
    padding: 2px;
    margin: -2px;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .package-card:hover::before {
    opacity: 1;
  }

  .typewriter {
    overflow: hidden;
    border-right: 3px solid var(--jamaica-green);
    white-space: nowrap;
    margin: 0 auto;
    animation: typing 3.5s steps(40, end), blink-caret 0.75s step-end infinite;
  }

  @keyframes typing {
    from { width: 0; }
    to { width: 100%; }
  }

  @keyframes blink-caret {
    from, to { border-color: transparent; }
    50% { border-color: var(--jamaica-green); }
  }

  .hero-overlay {
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.3) 100%);
  }

  .pricing-pulse {
    animation: pricePulse 2s ease-in-out infinite;
  }

  @keyframes pricePulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }

  .form-focus {
    transition: all 0.3s ease;
  }

  .form-focus:focus {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  }

  .nav-link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, var(--jamaica-green), var(--jamaica-yellow));
    transition: width 0.3s ease;
  }

  .nav-link:hover::after {
    width: 100%;
  }
}

