import { useState } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useBookingCalculator } from "@/hooks/use-booking-calculator";
import PackageCard from "@/components/package-card";
import { Camera, Heart, Users, Plus, Minus, ChevronDown, Shield, AlertTriangle, Clock, DollarSign, CheckCircle } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

const bookingFormSchema = z.object({
  clientName: z.string().min(1, "Client name is required"),
  email: z.string().email("Valid email is required"),
  contactNumber: z.string().min(1, "Contact number is required"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string().min(1, "Please confirm your password"),
  numberOfPeople: z.number().min(1, "At least 1 person is required"),
  shootDate: z.string().min(1, "Shoot date is required"),
  shootTime: z.string().min(1, "Shoot time is required"),
  location: z.string().min(1, "Location details are required"),
  parish: z.string().min(1, "Parish selection is required"),
  referralSource: z.array(z.string()).default([]),
  clientInitials: z.string().min(1, "Client initials are required").max(5, "Initials too long"),
  contractAccepted: z.boolean().refine((val) => val, "You must accept the contract terms"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type BookingFormData = z.infer<typeof bookingFormSchema>;

export default function BookingCalculator() {
  const { calculation, packages, eventHours, updateService, updatePackage, updatePeople, updateTransportation, updateEventHours, toggleAddon, toggleVideoPackage, updateVideoPackage } = useBookingCalculator();
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [isTermsOpen, setIsTermsOpen] = useState(false);

  const form = useForm<BookingFormData>({
    resolver: zodResolver(bookingFormSchema),
    defaultValues: {
      clientName: "",
      email: "",
      contactNumber: "",
      password: "",
      confirmPassword: "",
      numberOfPeople: 1,
      shootDate: "",
      shootTime: "",
      location: "",
      parish: "manchester-stelizabeth",
      referralSource: [],
      clientInitials: "",
      contractAccepted: false,
    },
  });

  const createBookingMutation = useMutation({
    mutationFn: async (data: BookingFormData) => {
      // Remove depositAmount and balanceDue - these are calculated server-side
      const bookingData = {
        ...data,
        numberOfPeople: Number(data.numberOfPeople), // Ensure it's a number
        serviceType: calculation.serviceType,
        packageType: calculation.packageType,
        hasPhotoPackage: calculation.hasPhotoPackage,
        hasVideoPackage: calculation.hasVideoPackage,
        videoPackageType: calculation.videoPackageType,
        transportationFee: calculation.transportationFee,
        addons: calculation.addons,
        totalPrice: calculation.totalPrice,
      };

      return apiRequest('POST', '/api/bookings', bookingData);
    },
    onSuccess: async (response) => {
      try {
        console.log('Booking response:', response);
        const result = await response.json();
        console.log('Booking result:', result);

        if (!result.booking || !result.booking.id) {
          throw new Error('Invalid booking response structure');
        }

        toast({
          title: "Booking Confirmed!",
          description: "Redirecting to payment...",
        });

        // Small delay to show toast before redirect
        setTimeout(() => {
          const paymentUrl = `/payment?booking=${result.booking.id}&type=deposit`;
          console.log('Redirecting to:', paymentUrl);
          navigate(paymentUrl);
        }, 1000);

      } catch (error) {
        console.error('Error in booking success:', error);
        toast({
          title: "Booking Created!",
          description: "There was an issue with payment redirect. Please contact us to complete payment.",
          variant: "destructive",
        });
      }
    },
    onError: async (error: any) => {
      console.error('Booking creation error:', error);
      console.error('Error details:', JSON.stringify(error, null, 2));

      // Try to extract meaningful error message from server response
      let errorMessage = "There was an error submitting your booking. Please try again.";

      try {
        // If the error has a response (HTTP error), try to parse it
        if (error.response) {
          const errorData = await error.response.json();

          if (errorData.details && Array.isArray(errorData.details)) {
            // Show specific Zod validation errors
            const fieldErrors = errorData.details.map((detail: any) => 
              `${detail.path?.join(' → ') || 'Unknown field'}: ${detail.message}`
            ).join('\n');
            errorMessage = `Validation errors:\n${fieldErrors}`;
          } else if (errorData.error) {
            errorMessage = errorData.error;
          }
        } else if (error.message) {
          errorMessage = error.message;
        }
      } catch (parseError) {
        console.error('Error parsing server response:', parseError);
      }

      toast({
        title: "Booking Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: BookingFormData) => {
    console.log('Form submission data:', data);
    console.log('Form validation errors:', form.formState.errors);
    console.log('Calculation data:', calculation);
    createBookingMutation.mutate(data);
  };

