      <div className="max-w-6xl mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold font-serif mb-4 gradient-text">Book Your Session</h2>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Choose your package and get instant pricing
          </p>
        </div>

        {/* Service Type Selection */}
        <div className="mb-12">
          <h3 className="text-2xl font-bold mb-6 text-center">Select Service Type</h3>
          <div className="grid md:grid-cols-3 gap-6">
            {serviceTypes.map((service) => (
              <Card
                key={service.id}
                className={`p-6 cursor-pointer hover-3d transition-all duration-300 ${
                  calculation.serviceType === service.id 
                    ? 'border-primary bg-primary/10 shadow-lg' 
                    : 'border-transparent hover:border-primary'
                }`}
                onClick={() => updateService(service.id)}
                data-testid={`service-${service.id}`}
              >
                <div className="text-center">
                  <div className={`w-12 h-12 bg-gradient-to-r ${service.gradient} rounded-full flex items-center justify-center mx-auto mb-4`}>
                    <service.icon className="text-white h-6 w-6" />
                  </div>
                  <h4 className="text-xl font-semibold mb-2">{service.name}</h4>
                  <p className="text-muted-foreground text-sm">{service.description}</p>
                </div>
              </Card>
            ))}
          </div>
        </div>

        {/* Package Selection */}
        {calculation.serviceType === 'photoshoot' && (
          <div className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-center">Photography Packages</h3>
            <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
              <PackageCard
                name="Bronze"
                price={packages.photoshoot.photography.bronze.price}
                duration={packages.photoshoot.photography.bronze.duration}
                images={packages.photoshoot.photography.bronze.images}
                locations={packages.photoshoot.photography.bronze.locations}
                color="bronze"
                features={[]}
                isSelected={calculation.packageType === 'bronze'}
                onClick={() => updatePackage('bronze')}
              />
              <PackageCard
                name="Silver"
                price={packages.photoshoot.photography.silver.price}
                duration={packages.photoshoot.photography.silver.duration}
                images={packages.photoshoot.photography.silver.images}
                locations={packages.photoshoot.photography.silver.locations}
                color="silver"
                features={[]}
                isSelected={calculation.packageType === 'silver'}
                onClick={() => updatePackage('silver')}
              />
              <PackageCard
                name="Gold"
                price={packages.photoshoot.photography.gold.price}
                duration={packages.photoshoot.photography.gold.duration}
